mapper.update:
update article
    set name = #{name}, title = #{title}, message = #{message}, image = #{image}, html = #{html}
where id = #{id}

mapper.select:
select id, name, title, message, date_format(time,'%Y-%m') as month, time
    from article
    where deleted = #{deleted}
order by time desc;

select date_format(time,'%Y-%m') time
    from article
    where deleted = #{deleted}
    group by date_format(time, '%Y-%m')
order by time desc;
